import {useState, useEffect} from "react";

const App = () => {
  const [value, setValue] = useState(null);
  const [ message, setMessage ] = useState(null);
  const [ prevChats, setPrevChats ] = useState([]);
  const [ currTitle, setCurrTitle ] = useState([]);

  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({
        message: value,
      }),
      headers: {
        "Content-Type": "application/json",
      }
    };
    try {
      const resp = await fetch("http://localhost:8000/completions", options);
      const data = await resp.json();
      // console.log(data);
      setMessage(data.choices[0].message)
    } catch (error) {
      console.log(error);
    }
  }

  useEffect( () => {
        console.log(currTitle, value, message);
        if (!currTitle && value && message) { setCurrTitle(value) }
        if (currTitle &&  value && message) {
          setPrevChats(prevChats)
        }
      }, [message, currTitle]
  );

  return (
      <div className="app">
        <section className="side-bar">
          <button>+ New Chat</button>
          <ul className={"history"}>
            <li>History</li>
          </ul>
          <nav>
            <p id={"built-by"}>Built with ðŸ’› by Atharva Shivankar in Rochester NY</p>
          </nav>
        </section>

        <section className="main">
          <h1>AtharvaAI - Interactive AssistantðŸ’¡</h1>
          <ul className={"feed"}>
          </ul>

          <div className={"bottom-section"}>
            <div className={"input-container"}>
              <input value={value} onChange={(e)=> setValue(e.target.value)}/>
              <div id="submit" onClick={getMessages}>âž¢</div>
            </div>
            <p className={"info"}>
              A ChatGPT clone application, powered by OpenAI API. Developed By Atharva Shivankar.
              Personal project for experimenting with OpenAI's models and API endpoints, not intended for commercial use.
            </p>

          </div>
        </section>
      </div>
  );
}

export default App;
