import {useState, useEffect} from "react";

const App = () => {
  const [value, setValue] = useState(null);
  const [ message, setMessage ] = useState(null);
  const [ prevChats, setPrevChats ] = useState([]);
  const [ currTitle, setCurrTitle ] = useState(null);


  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({
        message: value,
      }),
      headers: {
        "Content-Type": "application/json",
      }
    };
    try {
      const resp = await fetch("http://localhost:8000/completions", options);
      const data = await resp.json();
      // console.log(data);
      setMessage(data.choices[0].message)
    } catch (error) {
      console.log(error);
    }
  }

    const handleSendButton = (event) => {
        getMessages();
        setValue("");
    }

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            getMessages();
            setValue("");
        }
    }

  const createNewChat = () => {
      setMessage(null);
      setValue("");
      setCurrTitle(null);
  }

  useEffect( () => {
        console.log(currTitle, value, message);
        if (!currTitle && value && message) { setCurrTitle(value) }
        if (currTitle &&  value && message) {
          setPrevChats(prevChats => [...prevChats, {
                title: currTitle,
                role: "user",
                content: value
              }, {
                title: currTitle,
                role: message.role,
                content: message.content
              }
            ]
          )
        }
      }, [message, currTitle]
  );

  console.log(prevChats);

  const currChat = prevChats.filter(prevChat => prevChat.title === currTitle);

  return (
      <div className="app">
        <section className="side-bar">
          <button onClick={createNewChat}>+ New Chat</button>
          <ul className={"history"}>
            <li>History</li>
          </ul>
          <nav>
            <p id={"built-by"}>Built with ðŸ’› by Atharva Shivankar in Rochester NY</p>
          </nav>
        </section>

        <section className="main">
          {!currTitle && <h1>AtharvaAI - Interactive AssistantðŸ’¡</h1>}
          <ul className={"feed"}>
              {currChat.map((chatMessage, index) => <li key={ index }>
                  <p className={"role"}>{chatMessage.role}</p>
              </li>  )}
          </ul>

          <div className={"bottom-section"}>
            <div className={"input-container"}>
              <input value={value} onChange={(e)=> setValue(e.target.value)} onKeyDown={handleKeyDown}/>
              <div id="submit" onClick={handleSendButton}>âž¢</div>
            </div>
            <p className={"info"}>
              A ChatGPT clone application, powered by OpenAI API. Developed By Atharva Shivankar.
              Personal project for experimenting with OpenAI's models and API endpoints, not intended for commercial use.
            </p>

          </div>
        </section>
      </div>
  );
}

export default App;
